import { useState, useRef, useEffect } from 'react';

var mapboxgl = require('mapbox-gl/dist/mapbox-gl.js');
var marker = require('mapbox-gl/dist/mapbox-gl.js');
mapboxgl.accessToken = 'pk.eyJ1IjoiYWlkenNoYWtlIiwiYSI6ImNsYW93NzBtZTBtNHYzdG9hM21tNHB0ZjkifQ.h-TKUdMdz0KeFbwbAzdtGg';

type MapProps = {
    geocode: {[key: number]: any}
}

export function Map ({geocode}: MapProps) {
    const mapContainer = useRef<any>(null);
    const map = useRef<any>(null);
    const [ coords, setCoords ] = useState({
        lat: 35.6762,
        long: 139.6503,
        zoom: 10
    })

    useEffect(() => {
        if (map.current) return;
        map.current = new mapboxgl.Map({
            container: mapContainer.current,
            style: 'mapbox://styles/mapbox/streets-v11',
            center: [coords.long, coords.lat],
            zoom: coords.zoom
        });
        var marker = new mapboxgl.Marker()
            .setLngLat([139.6503, 35.6762])
            .setPopup(
                new mapboxgl.Popup({ offset: 30 })
                    .setHTML('<p>' + 'Tokyo' + '<p>')
            )
            .addTo(map.current)
    });

    return (
        <div ref={mapContainer} style={{width: '100%', height:'50vh'}}></div>
    )
}
