import { Fragment } from 'react';
import { Card, CardImg, CardContainer, TagsContainer, CardTitle, ResultsContainer, CardButton, CardTag  } from './styles';
import { ModalDialog } from '@tablecheck/tablekit-modal-dialog';

type ModalProps = {
    handleClick: () => void,
    name: string,
    address: string[],
    alt_address: string[],
    banner_image: string,
    body: string,
    title: {[key: string]: any},
    phone: string,
    phone_natl: string,
    stations: {[key: string]: any},
    url: string
}

export function Modal ({handleClick, name, address, alt_address, banner_image, body, title, phone, phone_natl, stations, url}: ModalProps) {
    var banner;
    if (title[1].locale === 'en') {
        banner = <h3>title[1].translation</h3>
    } else if (title[0]) {
        banner = <h3>title[0].translation</h3>
    } else {
        return
    };

    return (
        <ModalDialog
            data-testid="Modal Test Id"
            hasCloseIcon
            maxWidth={{
                default: 200,
                'min-width: 600px': 400,
                'min-width: 800px': 700
            }}
            onCloseAutoFocus={function noRefCheck(){}}
            onEscapeKeyDown={function noRefCheck(){}}
            onOpenAutoFocus={function noRefCheck(){}}
            onPointerDownOutside={function noRefCheck(){}}
            trigger={<CardButton onClick={handleClick}>
            More
            </CardButton>}
            >
            <Fragment>
                <div className="banner">
                    <img src={banner_image} alt="Restaurant banner"/>
                    <h3>{banner}</h3>


                </div>

               <h1>{name}</h1>
            </Fragment>
            </ModalDialog>
    )
}
